        return d < 0? -1 : 1;
    }
}

struct Vector {
    double x, y;
    Vector(double x = 0, double y = 0): x(x), y(y){
    }

    bool operator<(const Vector& other) const {
        return dcmp(x - other.x) < 0 || (dcmp(x - other.x) == 0 && dcmp(y - other.y) < 0);
    }
};

typedef Vector Point;

double dot(const Vector& a, const Vector& b) {
    return a.x * b.x + a.y * b.y;
}

double cross(const Vector& a, const Vector& b) {
    return a.x * b.y - a.y * b.x; 
}

Vector operator+(const Vector& a, const Vector& b) {
    return Vector(a.x + b.x, a.y + b.y);
}

Vector operator-(const Vector& a, const Vector& b) {
    return Vector(a.x - b.x, a.y - b.y);
}

Vector operator*(const Vector& a, const double& p) {
    return Vector(a.x * p, a.y * p);
}

double angle(const Vector& a) {
    return atan2(a.y, a.x);
}

double length(const Vector& a) {
    return sqrt(dot(a, a));
}

struct Circle {
    Point c;
    double r;
    Circle(double x = 0, double y = 0,  double r = 0): c(Point(x, y)), r(r){}

    Point point(double rad) const {
        return Point(c.x + r * cos(rad), c.y + r * sin(rad));
    }
};


vector<Point> circleCross(const Circle& a, const Circle& b) {

    Vector v = b.c - a.c;

    double d = length(v);

    assert(d <= a.r + b.r);

    //b.r * b.r = d * d + a.r * a.r - 2 * d * a.r*cos(alpha);
    double x = acos((d * d + a.r * a.r - b.r * b.r)/(2 * d * a.r));

    double rad = angle(v);

    Point p1 = a.point(rad + x);
    Point p2 = a.point(rad - x);

    vector<Point> results{p1, p2};

    sort(results.begin(), results.end());

    return results;
}

