	if((ans[0].x > ans[1].x) || (fabs(ans[0].x-ans[1].x) < EPS && ans[0].y > ans[1].y)){
		swap(ans[0],ans[1]);
	}

	printf("%.10lf %.10lf %.10lf %.10lf\n",ans[0].x,ans[0].y,ans[1].x,ans[1].y);

	return 0;
}

