#undef ld
#define ld long double
const ld Eps = 1e-10;
struct Point{
	ld x,y;
	il ld Len2(){ return x*x+y*y; }
	il ld Len(){ return sqrt(x*x+y*y); }
};
il Point operator+( const Point &a , const Point &b ){ return {a.x+b.x,a.y+b.y}; }
il Point operator-( const Point &a , const Point &b ){ return {a.x-b.x,a.y-b.y}; }
il auto operator^( const Point &a , const Point &b ){ return a.x*b.x + a.y*b.y; }
il auto operator*( const Point &a , const Point &b ){ return a.x*b.y - b.x*a.y; }
il Point operator*( const Point &a , const ld &b ){ return {a.x*b,a.y*b}; }
il Point operator/( const Point &a , const ld &b ){ return {a.x/b,a.y/b}; }
il bool operator==( const Point &a , const Point &b ){ return a.x == b.x and a.y == b.y; }
il ld Dis( const Point &a , const Point &b ){ return sqrt((ld)Sqr(a.x-b.x)+Sqr(a.y-b.y)); }
il ld Dis2( const Point &a , const Point &b ){ return Sqr(a.x-b.x)+Sqr(a.y-b.y); }
ostream& operator<<( ostream &out , const Point &p ){
	out << p.x << " " << p.y << "  ";
	return out;
}
il bool OnSeg( const Point &a , const Point &b , const Point &c ){
	Point ab = b-a, ac = c-a;
	if( fabs(ab*ac) > Eps ) return 0;
	if( (ab^ac) < -Eps ) return 0;
	if( fabs(ab.y*ab.y+ab.x*ab.x) > fabs(ac.y*ac.y+ac.x*ac.x) ) return 0;
	// cout << a.x << " " << a.y << "  " << b.x << " " << b.y << "  " << c.x << " " << c.y << endl;
	return 1;
}
bool Ins( Point p1 , Point p2 , Point p3 , Point p4 ){
	if( p1 == p3 or p1 == p4 or p2 == p3 or p2 == p4 ) return 1;
	if( OnSeg(p1,p3,p2) or OnSeg(p1,p4,p2) or OnSeg(p3,p1,p4) or OnSeg(p3,p2,p4) ) return 1;
	if( ((p2-p1)*(p3-p1)<-Eps) == ((p2-p1)*(p4-p1)<-Eps) ) return 0;
	if( ((p4-p3)*(p1-p3)<-Eps) == ((p4-p3)*(p2-p3)<-Eps) ) return 0;
	return 1;
}
Point InsPoint( Point p1 , Point p2 , Point p3 , Point p4 ){
	ld d1 = (ld)abs((p2-p3)*(p3-p1)) / Dis(p1,p2);
	ld d2 = (ld)abs((p2-p4)*(p4-p1)) / Dis(p1,p2);
	ld ratio = d1 / (d1+d2);
	return (p4-p3)*ratio + p3;
}
Point H( Point a , Point b , Point p ){
	Point ab = b-a;
	ld dis2 = Dis2(a,b);
	Point ret = a + ab * (((p-a)^ab)/dis2);
	return ret;
}
ld Distance( Point p1 , Point p2 , Point p3 , Point p4 ){
	if(Ins(p1,p2,p3,p4)) return 0;
	ld ans = min({ Dis(p1,p3) , Dis(p1,p4) , Dis(p2,p3) , Dis(p2,p4) });
	auto Check = [&]( Point a , Point b , Point p ){
		Point h = H(a,b,p);
		if(!OnSeg(a,h,b)) return;
		ld tans = Dis(h,p);
		Mymin(ans,tans);
	};
	Check(p1,p2,p3);
	Check(p1,p2,p4);
	Check(p3,p4,p1);
	Check(p3,p4,p2);
	return ans;
}

