	Point o1,o2;
	ld r1,r2;
	cin >> o1.x >> o1.y >> r1;
	cin >> o2.x >> o2.y >> r2;

	ld d = Dis(o1,o2);
	ld x = (Sqr(r2)-Sqr(r1)-Sqr(d)) / (-2*d);
	Point o1h = (o2-o1)*(x/d);
	Point h = o1 + o1h;
	ld halen = sqrt(Sqr(r1)-Sqr(x));
	Point o1hfa = (o2-o1)/d;
	Point hafa{-o1hfa.y,o1hfa.x};
	Point ans_1 = h + hafa*halen;
	Point ans_2 = h - hafa*halen;

	if( ans_1.x < ans_2.x-Eps or ( fabs(ans_1.x-ans_2.x)<Eps and ans_1.y < ans_2.y ) ){}
	else swap(ans_1,ans_2);
	cout << ans_1 << endl << ans_2 << endl;

	END();
}

