//点を表す構造体
class Point{
public:
  double x, y;

  //点を直接指定するコンストラクタ
  Point(double x=0, double y=0){
    this->x = x;
    this->y = y;
  }

  static Point polar(double r, double a){
    return Point(r*cos(a), r*sin(a));
  }

  Point operator+(const Point &seg2){
    return Point(x+seg2.x, y+seg2.y);
  }

  Point operator-(const Point &seg2){
    return Point(x-seg2.x, y-seg2.y);
  }

  Point operator*(const double k){
    return Point(x*k, y*k);
  }

  Point operator/(const double k){
    return Point(x/k, y/k);
  }

  Point &operator=(const Point &p){
    x = p.x;
    y = p.y;
    return *this;
  }

  bool operator < (const Point &p) const{
    return x != p.x? x<p.x : y<p.y;
  }

  bool operator == (const Point &p) const{
    return equals(x, p.x) && equals(y, p.y);
  }

  double norm(){
    return x*x + y*y;
  }

  double abs(){
    return sqrt(norm());
  }

  static double dot(Point a, Point b){
    return a.x*b.x + a.y*b.y;
  }

  static double cross(Point a, Point b){
    return a.x*b.y - a.y*b.x;
  }

  static bool isOrthogonal(Point a, Point b){
    return equals(dot(a, b), 0.0);
  }

  static bool isParallel(Point a, Point b){
    return equals(cross(a, b), 0.0);
  }

  static void swap(Point* a, Point* b){
    Point temp = *b;
    *b = *a;
    *a = temp;
  }

  double arg(){
    return atan2(y, x);
  }
};

