class Polygon{
public:
  Point* ver;
  int size;
  Polygon(int size){
    this->size = size;
    ver = new Point[size];
  }
  ~Polygon(){
    delete[] ver;
  }

  double area(){
    double ans=0.0;

    Vector a, b;
    for(int i=0; i<size; i++){
      a=ver[i%size]; b=ver[(i+1)%size];
      ans += Vector::cross(a, b)/2;
    }

    return ans;
  }

  /*
    ・含まれる時:2
    ・辺上に点が存在する時:1
    ・それ以外の場合:0
    を返す
  */
  int contain(Point p){
    bool flag = false;

    int ans;
    for(int i=0; i<size; i++){
      Vector a = ver[i%size]-p, b = ver[(i+1)%size]-p;
      //外積=0かつ内積<0の時辺上に点があると判定できる
      if(fabs(Vector::cross(a, b))<EPS && Vector::dot(a, b)<EPS){
        ans = 1;
        return ans;
      }
      if(a.y > b.y)Vector::swap(&a, &b);
      //端点をうまく処理するために以下の条件分岐ではEPSの使い方に注意
      if(a.y < EPS && b.y > EPS && Vector::cross(a, b)>EPS) flag = !flag;
    }

    ans = flag ? 2 : 0;
    return ans;
  }
};

