	double a = 0, b = 0, s = 0, t = 0, u = 0,
		   v1 = 0, v2 = 0, w1 = 0, w2 = 0;

	if (c1y == c2y) {
		b = (c1x + c2x + (c1r * c1r - c2r * c2r) / (c2x - c1x)) / 2;
		v1 = b;
		v2 = b;
		w1 = c1y - sqrt(c1r * c1r - (b - c1x) * (b - c1x));
		w2 = c1y + sqrt(c1r * c1r - (b - c1x) * (b - c1x));
	}
	else {
		a = (c1x - c2x) / (c2y - c1y);
		b = (c1r * c1r - c2r * c2r - c1x * c1x - c1y * c1y + c2x * c2x + c2y * c2y) / (2 * (c2y - c1y));

		s = a * a + 1;
		t = a * b - a * c1y - c1x;
		u = c1x * c1x + (b - c1y) * (b - c1y) - c1r * c1r;

		v1 = (-t - sqrt(t * t - s * u)) / s;
		v2 = (-t + sqrt(t * t - s * u)) / s;
		w1 = a * v1 + b;
		w2 = a * v2 + b;
	}

