namespace asl
{
    template <typename numeric_type>
    class circle
    {
    public:
        point<numeric_type> p;
        numeric_type r;
    };

    template <typename numeric_type>
    std::istream &operator>>(std::istream &is, circle<numeric_type> &c)
    {
        return is >> c.p >> c.r;
    }

    template <typename numeric_type, typename float_type = double>
    std::vector<point<numeric_type>> intersect_circle_circle(const circle<numeric_type> &c, const circle<numeric_type> &d)
    {
        float_type dist = abs(c.p - d.p);
        if (sign(dist - c.r - d.r) > 0)
            return {}; 
        if (sign(dist - std::abs(c.r - d.r)) < 0)
            return {}; 
        float_type a = (c.r * c.r - d.r * d.r + dist * dist) / (2 * dist);
        float_type h = sqrt(c.r * c.r - a * a);
        auto v = (d.p - c.p) / dist;
        if (sign(h) == 0)
            return {c.p + v * a};                                
        return {c.p + v * a + point<numeric_type>(0, 1) * v * h, 
                c.p + v * a - point<numeric_type>(0, 1) * v * h};
    }

} 

