	cin>>c1.c.x>>c1.c.y>>c1.r;
	cin>>c2.c.x>>c2.c.y>>c2.r;

	ans = getCrosPoint(c1, c2);

	if(ans.first > ans.second) swap(ans.first, ans.second);


	cout<<ans.first.x<<" "<<ans.first.y<<" "<<ans.second.x<<" "<<ans.second.y<<endl;

	return 0;
}

