  bool operator < ( const Vector &v ) const { return x * x + y * y < v.x * v.x + v.y * v.y; }

  double abs() { return sqrt( norm() ); }
  double norm() { return x * x + y * y; }

  static double dot( Vector v1, Vector v2 ) { return v1.x * v2.x + v1.y * v2.y; }
  static double cross( Vector v1, Vector v2 ) { return v1.x * v2.y - v1.y * v2.x; }
  void print() { cout << fixed << setprecision(10) << x << " " << y; }
};

class Circle
{
public:
  Vector cp;
  double r;
  Circle(){}
  Circle( Vector cp, double r ) : cp(cp), r(r) {}
};

class CPCircles
{
public:
  Circle c0, c1;
  CPCircles(){}
  CPCircles( Circle c0, Circle c1 ) : c0(c0), c1(c1) {}
  int solve();
  void print( Vector x, Vector y );
};

void CPCircles::print( Vector x, Vector y )
{
  if ( x.x < y.x )
    {
      x.print();
      cout << " ";
      y.print();
      cout << endl;
    }
  else if ( x.x > y.x )
    {
      y.print();
      cout << " ";
      x.print();
      cout << endl;
    }
  else if ( x.y < y.y )
    {
      x.print();
      cout << " ";
      y.print();
      cout << endl;
    }
  else
    {
      y.print();
      cout << " ";
      x.print();
      cout << endl;
    }
}

int CPCircles::solve()
{
  Vector p = c1.cp - c0.cp;

  Vector ev = p / p.abs();
  Vector eh = Vector( -ev.y, ev.x );

  double cs = ( p.norm() + c0.r * c0.r - c1.r * c1.r ) / ( 2 * p.abs() );
  double ss = sqrt( c0.r * c0.r - cs * cs );

  Vector cp0 = ev * cs + eh * ss;
  Vector cp1 = ev * cs - eh * ss;

  print( cp0, cp1 );
  return 0;
}

