	cin >> g.x >> g.y >> g.r;
	vector<pt_t> ans = cross_point(f, g);
	if (ans.size() == 1) ans.push_back(ans.back());
	sort(ans.begin(), ans.end());
	cout << ans[0].x << " " << ans[0].y << " " << ans[1].x << " " << ans[1].y << endl;
}

#include <iostream>
