pair<Point, Point> cross_point(Circle c1, Circle c2){
    double d = sqrt(dot(c2.c - c1.c, c2.c - c1.c));
    double cos1 = (c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d);
    double theta1 = acos(cos1);
    double theta2 = atan2(c2.c.y - c1.c.y, c2.c.x - c1.c.x);
    Point p1(c1.r * cos(theta2 + theta1), c1.r * sin(theta2 + theta1));
    Point p2(c1.r * cos(theta2 - theta1), c1.r * sin(theta2 - theta1));
    return make_pair(p1, p2);
}
