    Circle c2 = Circle(Point(x2, y2), r2);
    auto res = getCrossPoint(c1, c2);
    Point a = res.first, b = res.second;
    if(a < b) {}
    else swap(a, b);
    cout << a << " " << b << endl;
    return 0;
}

