Points crosspoint(const Circle &c1, const Circle &c2){
  Points res;
  DD d = abs(c1-c2);
  if(d < eps) return Points();
  if(d > c1.r + c2.r + eps) return Points();
  DD a = acos((c1.r*c1.r + d*d - c2.r*c2.r) / (2 * c1.r * d)); // 余弦定理
  DD t = atan2(c2.y - c1.y, c2.x - c1.x);
  Point p1 = c1 + Point(cos(t+a) * c1.r, sin(t+a) * c1.r);
  Point p2 = c1 + Point(cos(t-a) * c1.r, sin(t-a) * c1.r);
  res.pb(p1); res.pb(p2);
  // if(!eq(p1, p2)) res.pb(p2);
  return res;
}

