double dot(Vector a, Vector b){
    return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b){
    return a.x * b.y - a.y * b.x;
}

Point projection(Segment s, Point p){
    Vector base = s.p2 - s.p1;
    double r = dot(p-s.p1, base) / base.norm();
    return s.p1 + base * r;
}

int ccw(Point p0, Point p1, Point p2){
    Vector a = p1 - p0, b = p2 - p0;
    if (cross(a, b) > EPS) return 1;
    if (cross(a, b) < -EPS) return -1;
    if (dot(a, b) < -EPS) return 2;
    if (a.norm() < b.norm()) return -2;
    return 0;
}

bool intersection(Point p0, Point p1, Point p2, Point p3){
    if (ccw(p0, p1, p2) == 0 || ccw(p0, p1, p3) == 0 || 
        ccw(p2, p3, p0) == 0 || ccw(p2, p3, p1) == 0)
        return true;
    if (ccw(p0, p1, p2) * ccw(p0, p1, p3) == -1 && ccw(p2, p3, p0) * ccw(p2, p3, p1) == -1)
        return true;
    return false;
}

pair<Point, Point> getCrossPoints(Circle c, Line l){
    Vector pr = projection(l, c.c);
    Vector e = (l.p2 - l.p1) / (l.p2 - l.p1).abs();
    double base = sqrt(c.r * c.r - (pr - c.c).norm());
    return make_pair(pr + e * base, pr - e * base);
}

