double add(double a,double b){
	if(fabs(a+b)<EPS*(fabs(a)+fabs(b)))return 0.0;
	return a+b;
}

class P{
public:
	double x,y;
	P(double xx=0.0,double yy=0.0){
		x=xx;
		y=yy;
	}
	P operator +(P p){
		return P(add(x,p.x),add(y,p.y));
	}
	P operator -(P p){
		return P(add(x,-p.x),add(y,-p.y));
	}
	bool operator<(const P &p)const{
		if(add(x,-p.x)==0.0)return y<p.y;
		return x<p.x;
	}
};

typedef P Vector;

double norm(P p){
	return p.x*p.x+p.y*p.y;
}

double abs(P p){
	return sqrt(norm(p));
}

class Circle{
public:
	P c;
	double r;
	Circle(P pp=P(),double rr=0.0){
		c=pp;
		r=rr;
	}
};

double arg(Vector p){
	return atan2(p.y,p.x);
}

Vector polar(double a,double r){
	return P(cos(r)*a,sin(r)*a);
}

bool intersect(Circle a,Circle b){
	return add(add(a.r,b.r),-abs(a.c-b.c))>=0.0;
}

pair<P,P> getCrossPoints(Circle c1,Circle c2){
	assert(intersect(c1,c2));
	double d=abs(c1.c-c2.c);
	double a=acos((c1.r*c1.r+d*d-c2.r*c2.r)/(2*c1.r*d));
	double t=arg(c2.c-c1.c);
	return make_pair(c1.c+polar(c1.r,t+a),c1.c+polar(c1.r,t-a));
}

