//反時計回りCCW
static const int COUNTER_CLOCKWISE=1;
static const int CLOCKWISE=-1;
static const int ONLINE_BACK=2;
static const int ONLINE_FRONT=-2;
static const int ON_SEGMENT=0;
//反時計回りCCW
int ccw(Point p0,Point p1,Point p2){
  Vector a=p1-p0;
  Vector b=p2-p0;
  if(cross(a,b)>EPS) return COUNTER_CLOCKWISE;
  if(cross(a,b)<-EPS) return CLOCKWISE;
  if(dot(a,b)<-EPS) return ONLINE_BACK;
  if(a.norm()<b.norm()) return ONLINE_FRONT;

  return ON_SEGMENT;
}

//線分p1p2と線分p3p4の交差判定
bool intersect(Point p1,Point p2,Point p3,Point p4){
  return( ccw(p1,p2,p3)*ccw(p1,p2,p4)<=0 &&
          ccw(p3,p4,p1)*ccw(p3,p4,p2)<=0);
}
bool intersect(Segment s1,Segment s2){
  return intersect(s1.p1,s1.p2,s2.p1,s2.p2);
}


//点aと点bの距離
double getDistace(Point a,Point b){
  return (a-b).abs();
}
//直線lと点pの距離
double getDistaceLP(Line l,Point p){
  return abs(cross(l.p2-l.p1,p-l.p1)/(l.p2-l.p1).abs());
} 
//線分sと点pの距離
double getDistaceSP(Segment s,Point p){
  if(dot(s.p2-s.p1,p-s.p1)<0.0) return (p-s.p1).abs();
  if(dot(s.p1-s.p2,p-s.p2)<0.0) return (p-s.p2).abs();
  return getDistaceLP(s,p);
}
//線分s1と線分s2の距離
double getDistace(Segment s1,Segment s2){
  if(intersect(s1,s2)) return 0.0;
  return min(min(getDistaceSP(s1,s2.p1),getDistaceSP(s1,s2.p2))
  ,min(getDistaceSP(s2,s1.p1),getDistaceSP(s2,s1.p2)));
}

//線分s1と線分s2の交点
Point getCrossPoint(Segment s1,Segment s2){
  Vector base=s1.p2-s1.p1;
  double d1=abs(cross(base,s2.p2-s2.p1));
  double d2=abs(cross(base,s1.p2-s2.p1));
  if(equals(fabs(d1),0.0) && equals(fabs(d2),0.0)) return s2.p1;
  return s2.p1+(s2.p2-s2.p1)*d2/d1;
}

//円cと線分lの交点
pair<Point,Point> getCrossPoints(Circle c,Line l){
  // assert(intersect(c,l));
  Vector pr=project(l,c.c);
  Vector e=(l.p2-l.p1)/(l.p2-l.p1).abs();
  double base=sqrt(c.r*c.r-(pr-c.c).norm());
  return make_pair(pr+e*base,pr-e*base);
}

//円と円の交点
double arg(Vector p) { return atan2(p.y,p.x); }
Vector polar(double a,double r) {return Point(cos(r)*a,sin(r)*a); }
//円c1と円c2の交点
pair<Point,Point> getCrossPoints(Circle c1,Circle c2){
  double d=(c1.c-c2.c).abs();
  double a=acos((c1.r*c1.r+d*d-c2.r*c2.r)/(2*c1.r*d));
  double t=arg(c2.c-c1.c);
  return make_pair(c1.c+polar(c1.r,t+a),c1.c+polar(c1.r,t-a));
}

