  Circle c1(Point(c1x,c1y),c1r),c2(Point(c2x,c2y),c2r);
  auto p=getCrossPoints(c1,c2);
  Point p2,p3;
  tie(p2,p3)=p;
  if(p2>p3){
    swap(p2,p3);
  }
  cout<<fixed<<setprecision(15)<<p2.x<<' '<<p2.y<<' '<<p3.x<<' '<<p3.y<<'\n';
  return(0);
}

