}

bool isIntersectCL(Circle c,Line l){
    return getDistanceLP(l,c.p)<=c.r+EPS;
}

bool isIntersectCC(Circle c1,Circle c2){
    return abs(c1.p-c2.p)<=c1.r+c2.r+EPS;
}

pair<Point,Point>getCrossPointsCL(Circle c,Line l){
    assert(isIntersectCL(c,l));
    Vector pr=project(l,c.p);
    Vector e=(l.p2-l.p1)/abs(l.p2-l.p1);
    double base=sqrt(square(c.r)-norm(pr-c.p));
    return make_pair(pr+e*base,pr-e*base);
}

pair<Point,Point>getCrossPointsCC(Circle c1,Circle c2){
    assert(isIntersectCC(c1,c2));
    double d=abs(c1.p-c2.p);
    double a=acos((square(c1.r)+square(d)-square(c2.r))/(2*c1.r*d));
    double t=arg(c2.p-c1.p);

    return make_pair(c1.p+polar(c1.r,t+a),c1.p+polar(c1.r,t-a));
}

