// 内積の計算をする関数
double dot(Vector a, Vector b)
{
    return ( ( a.x * b.x ) + ( a.y * b.y ) );
}

// 外積の計算をする関数
double cross(Vector a, Vector b)
{
    return ( ( a.x * b.y ) - ( a.y * b.x ) );
}

// 外積の計算をする関数 2
// 直線 : line に対する点pの外積の演算
double cross_point(Line line, Point pp)
{
    return ( line.p2.x - line.p1.x ) * ( pp.y - line.p1.y ) - ( pp.x - line.p1.x ) * ( line.p2.y - line.p1.y );
}

// 正射影ベクトルを求める関数
// p : 垂線の始点となる点
Vector projection(Vector a, Vector b, Vector p)
{
    // 正射影ベクトルの考え方より
    // 正射影ベクトル = ( ( a, bベクトルの内積 ) / aベクトルの大きさの2乗 ) * aベクトル
    return ( ( a * ( dot(a, b) / pow( a.distance(), 2.0 ) ) ) + p );
}

// 正射影ベクトルを求める関数 2
// p : 垂線の始点となる点
// 戻り値 : pからの垂線と直線：sの交点
Point project(Segment s, Point p)
{
    Vector base = s.p2 - s.p1;
    double r = dot(p - s.p1, base) / base.norm();
    Point tp = base * r;
    return s.p1 + tp;
}


// 点と直線の距離を求める関数
double getDistanceLP(Line l, Point p)
{
    if( dot( l.p2 - l.p1, p - l.p1 ) < 0.0 )
    {
        // 内積の値が負の場合, 2つの線分のなす角が90°以上のため,
        // 距離は, p と l.p1 の距離そのものになる
        return ( p - l.p1 ).distance();
    }

    if( dot( l.p1 - l.p2, p - l.p2 ) < 0.0 )
    {
        // 内積の値が負の場合, 2つの線分のなす角が90°以上のため,
        // 距離は, p と l.p2 の距離そのものになる
        return ( p - l.p2 ).distance();
    }

    // 2つの線分のなす角が90°以内の時
    return abs( cross(l.p2 - l.p1, p - l.p1) / ( l.p2 - l.p1 ).distance() );
}

static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;

// 3点の関係を調査する関数
int ccw(Point p0, Point p1, Point p2)
{
    Vector a = p1 - p0;
    Vector b = p2 - p0;

    if( cross(a, b) > EPS )
    {
        // P2が半時計回りの方向にいる
        return COUNTER_CLOCKWISE;
    }
    else if( cross(a, b) < -EPS )
    {
        // P2が時計回りの方向にいる
        return CLOCKWISE;
    }
    else if( dot(a, b) < -EPS )
    {
        // P2が, 直線：p0p1に対して, 180°反対方向にいる
        return ONLINE_BACK;
    }
    else if( a.norm() < b.norm() )
    {
        // p0, p1, p2 の順で同一直線上に点が並んでいる
        return ONLINE_FRONT;
    }

    // p0, p2, p1 の順で同一直線上に点が並んでいる
    return ON_SEGMENT;
}

// -----▼▼▼ 線分の交差判定を実施する関数群 ▼▼▼-----

bool intersect(Point p1, Point p2, Point p3, Point p4)
{
    return ( ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0
            && ccw(p3, p4, p1) * ccw(p3, p4, p1) <= 0 );
}

// 線分の交点が存在するかの確認
bool intersect(Segment s1, Segment s2)
{
    return intersect(s1.p1, s1.p2, s2.p1, s2.p2);
}

// 円の交点が存在するかの確認
bool intersect(Circle c1, Circle c2)
{
    return ( ( c2.c - c1.c ).distance() <= c1.r + c2.r );
}

// -----▲▲▲ 線分の交差判定を実施する関数群 ▲▲▲-----


// 線分の交点を求める関数
Point getCrossPoint(Segment s1, Segment s2)
{
	Point p0;
    double d1, d2, t;

    Vector base = s2.p2 - s2.p1;

    // 外積(平行四辺形の面積)から, 点と直線の距離を求める
    d1 = abs( cross(base, s1.p1 - s2.p1) / base.distance() );
    d2 = abs( cross(base, s1.p2 - s2.p1) / base.distance() );

    t = d1 / ( d1 + d2 );

    p0 = ( s1.p2 - s1.p1 ) * t;

    return s1.p1 + p0;
}

// 円と直線の交点を求める関数
pair<Point, Point> getCrossPoint(Circle circle, Line l)
{
    Point p0, p1;

    // 円の中心からの垂線と直線：lの交点：prを求める
    Vector pr = project(l, circle.c);

    if( ( pr - circle.c ).distance() > circle.r )
    {
        // 円と直線に交点が存在しない時
        return make_pair(p0, p1);
    }

    // 直線：l上の単位ベクトルを求める
    Vector e = ( l.p2 - l.p1 ) / ( l.p2 - l.p1 ).distance();
    // 交点：prと、円と直線の交点までの距離を求める
    double base = sqrt( circle.r * circle.r - ( pr - circle.c ).norm() );

    // 円と直線の交点を求める
    Point eb = e * base;
    p0 = pr - eb;
    p1 = pr + eb;

    if( p1 < p0 )
    {
        // 点の大小関係の調整
        p0 = pr + eb;
        p1 = pr - eb;
    }

    return make_pair(p0, p1);
}

// -----▼▼▼ 円の交点を求めるための関数一覧 ▼▼▼-----

// 与えられたベクトルのなす角を求める関数
double arg(Vector v)
{
    return atan2( v.y, v.x ) ;
}

// x軸上の点aを, x軸回りに角度θだけ回転させた点を求める関数
Point polar(double a, double theta)
{
    return Point(cos(theta) * a, sin(theta) * a);
}

// 2つの円の交点を求める関数
pair<Point, Point> getCrossPoint(Circle c1, Circle c2)
{
    Point p0, p1, pa, pb;

    assert(intersect(c1, c2));
    double d = (c1.c - c2.c).distance();
    // 余弦定理より
    double a = acos( ( c1.r * c1.r + d * d - c2.r * c2.r ) / ( 2.0 * c1.r * d ) );
    // x軸とのなす角を求める
    double t = arg(c2.c - c1.c);

    pa = polar(c1.r, t + a);
    p0 = c1.c + pa;
    pb = polar(c1.r, t - a);
    p1 = c1.c + pb;

    if( p1 < p0)
    {
        p1 = c1.c + pa;
        p0 = c1.c + pb;
    }

    return make_pair(p0, p1);
}

