  vector<point> res;
  b-=a;
  //if(abs(abs(b)-(ra+rb))>EPS)return res;
  double c=(ra+rb+abs(b))/2;
  c=2*sqrt(c*(c-ra)*(c-rb)*(c-abs(b)))/abs(b);
  double d=sqrt(ra*ra-c*c);
  double e=sqrt( c*c + (d+abs(b)) * (d+abs(b)) );


  if( ra*ra+norm(b) <e*e ) d*=-1;

  res.push_back(point(d,c));
  res.push_back(point(d,-c));  
  for(int i=0;i<(int)res.size();i++)res[i]=res[i]*b/abs(b)+a;
  return res;
}


