class Circle {
public:
    Point c;
    double r;
    Circle(double cx = 0.0, double cy = 0.0, double r = 0.0) : c(cx, cy), r(r) {}
    Circle(Point c = Point(), double r = 0.0) : c(c), r(r) {}
};

vector<Point> cross_point(const Circle& c1, const Circle& c2) {
    vector<Point> ans;

    double d = Point::norm(c2.c - c1.c);
    Point vec((c2.c - c1.c) / d);

    double x = (d * d + c1.r * c1.r - c2.r * c2.r) / (2 * d);
    Point mid = (c1.c * (d - x) + c2.c * x) / d;

    Point normal = Point(-vec.y, vec.x) * sqrt(c1.r * c1.r - x * x);
    ans.push_back(mid - normal);
    ans.push_back(mid + normal);

    return ans;
}

vector<Point> cross_point2(const Circle& c1, const Circle& c2) {
    vector<Point> ans;

    double d = Point::norm(c2.c - c1.c);
    Point vec(c2.c - c1.c);

    double alpha = acos(c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d);
    double t = atan(vec.y / vec.x);

    double cos1 = cos(alpha + t);
    double sin1 = sin(alpha + t);
    ans.push_back(c1.c + Point(cos1, sin1) * d);

    double cos2 = cos(-alpha + t);
    double sin2 = sin(-alpha + t);
    ans.push_back(c1.c + Point(cos2, sin2) * d);

    return ans;
}

