class Pt{
    public:
    double x, y;
    Pt(double x = 0, double y = 0):x(x),y(y){}
    Pt operator + (Pt p){return Pt(x + p.x, y + p.y);}
    Pt operator - (Pt p){return Pt(x - p.x, y - p.y);}
    Pt operator / (double d){return Pt(x / d, y / d);}
    Pt operator * (double d){return Pt(x * d, y * d);}
    Pt operator * (Pt p){return Pt(x * p.y, -y * p.x);}
    bool operator > (Pt p){return ((abs(x - p.x) > 1e-7) ? x > p.x: y > p.y);}
    double norm(){return hypot(x, y);}
    Pt rot90(){return Pt(-y, x);}
    Pt rot180(){return Pt(-x, -y);}
    Pt rot270(){return Pt(y, -x);}
    string str(){stringstream ss;ss<<x<<", "<<y;return ss.str();}
};

vector<Pt>cc_cross(Pt p1, double r1, Pt p2, double r2){
    double d = (p1 - p2).norm();
    double rc = (d * d + r1 * r1 - r2 * r2) / (2 * d);
    double rs = sqrt(max(r1 * r1 - rc * rc, 0.0));
    Pt diff = (p2 - p1) / d;
    vector<Pt>points = {p1 + diff * rc + diff.rot90() * rs, p1 + diff * rc + diff.rot270() * rs};
    return points;
}

