using namespace std;

inline double add(double a, double b){
    return abs(a+b)<(1e-10)*(abs(a)+abs(b)) ? 0.0 : a+b;
}

struct vec{
    double x,y;
    vec operator-(vec b){
        return (vec){add(x,-b.x),add(y,-b.y)};
    }
    vec operator+(vec b){
        return (vec){add(x,b.x),add(y,b.y)};
    }
    vec operator*(double d){
        return (vec){x*d,y*d};
    }
    double dot(vec v){
        return add(x*v.x,y*v.y);
    }
    double cross(vec v){
        return add(x*v.y,-y*v.x);
    }
    double norm(){
        return sqrt(x*x+y*y);
    }
};

pair<vec,vec> cross_point(vec& C1, vec& C2, double r1, double r2){
    vec e1,e2,P1,P2;
    double d=(C2-C1).norm();
    e1=(C2-C1)*(1/d);
    double theta = acos((r1*r1+(C2-C1).dot(C2-C1)-r2*r2)/(2*r1*d));
    e2.x=cos(theta)*e1.x-sin(theta)*e1.y, e2.y=sin(theta)*e1.x+cos(theta)*e1.y;
    P1=C1+e2*r1;
    e2.x=cos(theta)*e1.x+sin(theta)*e1.y, e2.y=-sin(theta)*e1.x+cos(theta)*e1.y;
    P2=C1+e2*r1;
    if(P1.x==P2.x){
        if(P1.y<P2.y) return make_pair(P1,P2);
        else return make_pair(P2,P1);
    }else{
        if(P1.x<P2.x) return make_pair(P1,P2);
        else return make_pair(P2,P1);
    }
}

int main(){
    vec C1,C2;
    double r1,r2;
    cin >> C1.x >> C1.y >> r1;
    cin >> C2.x >> C2.y >> r2;
    auto P = cross_point(C1,C2,r1,r2);
    cout.precision(8);
    cout << fixed;
    cout << P.first.x << " " << P.first.y << " " << P.second.x << " " << P.second.y << endl;
    return 0;
}

