#define EPSLON 1e-10
#define equals(a, b) (fabs((a) - (b)) < EPSLON)
#define COUNTER_CLOCKWISE 1
#define CLOCKWISE -1
#define ONLINE_BACK -2
#define ONLINE_FRONT 2
#define ON_SEGMENT 0

using namespace std;

// 点
class Point {
public:
    double x;
    double y;

    Point() {}

    Point(double x, double y) : x(x), y(y) {}

    Point operator+(Point a) {
        return Point(x + a.x, y + a.y);
    }

    Point operator-(Point a) {
        return Point(x - a.x, y - a.y);
    }

    Point operator*(double a) {
        return Point(x * a, y * a);
    }

    Point operator/(double a) {
        return Point(x / a, y / a);
    }

    double norm() {
        return x * x + y * y;
    }

    double absVector() {
        return sqrt(norm());
    }

    bool operator==(Point a) const {
        return equals(x, a.x) && equals(y, a.y);
    }
};

typedef Point Vector;

// 线段
class Segment {
public:
    Point a, b;
};

typedef Segment Line;

class Circle {
public:
    Point c;
    double r;
};

double dot(Vector a, Vector b) {
    return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b) {
    return a.x * b.y - a.y * b.x;
}

double arg(Vector p) {
    return atan2(p.y, p.x);
}

Vector polar(double a, double r) {
    return Point(a * cos(r), a * sin(r));
}

pair<Point, Point> getCrossPoints(Circle c1, Circle c2) {
    double d = (c2.c - c1.c).absVector();
    double t = arg(c2.c - c1.c);
    double a = acos((c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d));
    return make_pair(c1.c + polar(c1.r, t + a), c1.c + polar(c1.r, t - a));
}

int main() {
    Circle c1, c2;
    double x, y, r;
    scanf("%lf %lf %lf", &x, &y, &r);
    c1.c.x = x;
    c1.c.y = y;
    c1.r = r;
    double x1, y1, r1;
    scanf("%lf %lf %lf", &x1, &y1, &r1);
    c2.c.x = x1;
    c2.c.y = y1;
    c2.r = r1;
    pair<Point, Point> re = getCrossPoints(c1, c2);
    if (re.first.x == re.second.x && re.first.y == re.second.y) {
        printf("%.8lf %.8lf %.8lf %.8lf\n", re.first.x, re.first.y, re.second.x, re.second.y);
    } else if (re.first.x < re.second.x) {
        printf("%.8lf %.8lf %.8lf %.8lf\n", re.first.x, re.first.y, re.second.x, re.second.y);
    } else {
        if (re.first.x == re.second.x) {
            if (re.first.y < re.second.y) {
                printf("%.8lf %.8lf %.8lf %.8lf\n", re.first.x, re.first.y, re.second.x, re.second.y);
            } else {
                printf("%.8lf %.8lf %.8lf %.8lf\n", re.second.x, re.second.y, re.first.x, re.first.y);
            }
        } else {
            printf("%.8lf %.8lf %.8lf %.8lf\n", re.second.x, re.second.y, re.first.x, re.first.y);
        }
    }
}

#include <iostream>
#include <iomanip>
#include <algorithm>
#include <cmath>
#include <cassert>

#define EPS (1e-10)
#define equals(a, b) (fabs((a) - (b)) < EPS)

using namespace std;

static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;

class Point {
public:
	double x, y;
	Point(double x = 0, double y = 0) : x(x), y(y) {}

	Point operator + (Point p) { return Point(x + p.x, y + p.y); }
	Point operator - (Point p) { return Point(x - p.x, y - p.y); }
	Point operator * (double a) { return Point(a*x, a*y); }
	Point operator / (double a) { return Point(x / a, y / a); }

	double norm() { return x*x + y*y; }
	double abs() { return sqrt(norm()); }

	bool operator < (const Point &p) const {
		return x != p.x ? x < p.x : y < p.y;
	}

	bool operator == (const Point &p) const {
		return fabs(x - p.x) < EPS && fabs(y - p.y) < EPS;
	}
};

typedef Point Vector;

struct Segment {
	Point p1, p2;
	Segment() {}
};

typedef Segment Line;

class Circle {
public:
	Point c;
	double r;
	Circle(Point c = Point(), double r = 0.0) : c(c), r(r) {}
};


double norm(Vector a) {
	return a.norm();
}

double abs(Vector a) {
	return a.abs();
}

double dot(Vector a, Vector b) {
	return a.x*b.x + a.y*b.y;
}

double cross(Vector a, Vector b) {
	return a.x*b.y - a.y*b.x;
}

bool isOrthogonal(Vector a, Vector b) {
	return equals(dot(a, b), 0.0);
}

bool isOrthogonal(Point a1, Point a2, Point b1, Point b2) {
	return isOrthogonal(a1 - a2, b1 - b2);
}

bool isOrthogonal(Segment s1, Segment s2) {
	return isOrthogonal(s1.p1, s1.p2, s2.p1, s2.p2);
}

bool isParallel(Vector a, Vector b) {
	return equals(cross(a, b), 0.0);
}

bool isParallel(Point a1, Point a2, Point b1, Point b2) {
	return isParallel(a1 - a2, b1 - b2);
}

bool isParallel(Segment s1, Segment s2) {
	return isParallel(s1.p1, s1.p2, s2.p1, s2.p2);
}

Point project(Segment s, Point p) {
	double alpha = dot(p - s.p2, s.p1 - s.p2) / (s.p1 - s.p2).norm();
	return s.p2 + (s.p1 - s.p2)*alpha;
}

Point reflect(Segment s, Point p) {
	return project(s, p)*2.0 - p;
}

int ccw(Point p0, Point p1, Point p2) {
	Vector a = p1 - p0;
	Vector b = p2 - p0;
	if (cross(a, b) > EPS) return COUNTER_CLOCKWISE;
	if (cross(a, b) < -EPS) return CLOCKWISE;
	if (dot(a, b) < -EPS) return ONLINE_BACK;
	if (a.norm() < b.norm()) return ONLINE_FRONT;
	return ON_SEGMENT;
}

bool intersect(Point p1, Point p2, Point p3, Point p4) {
	int p1p2p3, p1p2p4, p3p4p1, p3p4p2;
	p1p2p3 = ccw(p1, p2, p3);
	p1p2p4 = ccw(p1, p2, p4);
	p3p4p1 = ccw(p3, p4, p1);
	p3p4p2 = ccw(p3, p4, p2);

	if (p1p2p3 == ON_SEGMENT) return true;
	if (p1p2p4 == ON_SEGMENT) return true;
	if (p3p4p1 == ON_SEGMENT) return true;
	if (p3p4p2 == ON_SEGMENT) return true;
	if (p1p2p3 == ONLINE_FRONT && p1p2p4 == ONLINE_BACK) return true;
	if (p1p2p3 == ONLINE_BACK && p1p2p4 == ONLINE_FRONT) return true;
	if (p1p2p3 == COUNTER_CLOCKWISE && p1p2p4 == CLOCKWISE && p3p4p1 == CLOCKWISE && p3p4p2 == COUNTER_CLOCKWISE) return true;
	if (p1p2p3 == CLOCKWISE && p1p2p4 == COUNTER_CLOCKWISE && p3p4p1 == COUNTER_CLOCKWISE && p3p4p2 == CLOCKWISE) return true;
	return false;
}

bool intersect(Segment s1, Segment s2) {
	return intersect(s1.p1, s1.p2, s2.p1, s2.p2);
}

bool intersect(Circle c, Line l) {
	return (project(l, c.c) - c.c).norm() <= c.r*c.r;
}

double getDistance(Point a, Point b) {
	return (a - b).abs();
}

double getDistanceLP(Line l, Point p) {
	return abs(cross(l.p2 - l.p1, p - l.p1) / (l.p2 - l.p1).abs());
}

double getDistanceSP(Segment s, Point p) {
	if (dot(s.p2 - s.p1, p - s.p1) < 0.0) return (p - s.p1).abs();
	if (dot(s.p1 - s.p2, p - s.p2) < 0.0) return (p - s.p2).abs();
	return getDistanceLP(s, p);
}

double getDistance(Segment s1, Segment s2) {
	if (intersect(s1, s2)) return 0.0;
	return min(min(getDistanceSP(s1, s2.p1), getDistanceSP(s1, s2.p2)), min(getDistanceSP(s2, s1.p1), getDistanceSP(s2, s1.p2)));
}

Point getCrossPoint(Segment s1, Segment s2) {
	Vector base = s2.p2 - s2.p1;
	double d1 = abs(cross(base, s1.p1 - s2.p1));
	double d2 = abs(cross(base, s1.p2 - s2.p1));
	double t = d1 / (d1 + d2);
	return s1.p1 + (s1.p2 - s1.p1)*t;

}

pair<Point, Point> getCrossPoints(Circle c, Line l) {
	assert(intersect(c, l));
	Vector pr = project(l, c.c);
	Vector e = (l.p2 - l.p1) / abs(l.p2 - l.p1);
	double base = sqrt(c.r*c.r - (pr - c.c).norm());
	return make_pair(pr + e*base, pr - e*base);
}

Vector rotate90(Vector v) {
	Vector w;
	w.x = -v.y; w.y = v.x;
	return w;
}

pair<Point, Point> getCrossPoints(Circle c1, Circle c2) {
	Vector v = c1.c - c2.c;
	Vector w = rotate90(v);
	double d = v.abs();
	double x = 0.5*(c1.r*c1.r + d*d - c2.r*c2.r) / d;
	double y = sqrt(c2.r*c2.r - x*x);
	Vector v1, v2;
	v1 = c2.c + v*(x / d);
	v2 = w*(y / w.abs());
	return make_pair(v1 + v2, v1 - v2);
}

