    return real(conj(a)*b);
}

struct Line: public pair<Point, Point> {
    Line() {}
    Line(const Point &a, const Point &b) {
        first = a;
        second = b;
    }
};

struct Circle {
    Point p;
    double r;
    Circle() {}
    Circle(const Point &p, const double r): p(p), r(r) {}
};

pair<Point, Point> get_cpoints(const Circle &c1, const Circle &c2) {
    Point p=c2.p-c1.p;

    double R=norm(p);
    double a=(R+(c1.r*c1.r)-(c2.r*c2.r))/2;
    double D=sqrt(R*(c1.r*c1.r)-a*a);

    Point p1=c1.p+p/R*Point(a, -D);
    Point p2=c1.p+p/R*Point(a, D);

    if (p2 < p1)
        swap(p1, p2);

    return make_pair(p1, p2);
}

int main() {
