    scanf("%lf %lf %lf", &x, &y, &r);
    Circle c1(Point(x, y), r);

    scanf("%lf %lf %lf", &x, &y, &r);
    Circle c2(Point(x, y), r);

    pair<Point, Point> cpoints=get_cpoints(c1, c2);
    printf("%.9f %.9f ", real(cpoints.first), imag(cpoints.first));
    printf("%.9f %.9f\n", real(cpoints.second), imag(cpoints.second));
