const double EPS = (1e-10);

class Point {
public:
    double x, y;

    Point(double x = 0, double y = 0): x(x), y(y) {}

    Point operator + (Point p) {
        return Point(x+p.x, y+p.y);
    }
    Point operator - (Point p) {
        return Point(x-p.x, y-p.y);
    }
    Point operator * (double a) {
        return Point(x*a, y*a);
    }
    Point operator / (double a) {
        return Point(x/a, y/a);
    }

    double abs() {
        return sqrt(norm());
    }
    double norm() {
        return x*x + y*y;
    }

    bool operator < (const Point &p) const {
        return x != p.x ? x < p.x : y < p.y;
    }

    bool operator == (const Point &p) const {
        return fabs(x-p.x) < EPS && fabs(y-p.y) < EPS;
    }
};

typedef Point Vector;
// 内積
double dot(Vector a, Vector b) {
    return a.x*b.x + a.y*b.y;
}
// 外積
double cross(Vector a, Vector b) {
    return a.x*b.y - a.y*b.x;
}

class Segment {
public:
    Point p1, p2;

    Segment(Point p1, Point p2): p1(p1), p2(p2) {}
};
typedef Segment Line;

Point project(Segment s, Point p) {
    Vector base = s.p2 - s.p1;
    Vector hypo = p - s.p1;
    double r = dot(hypo, base) / base.norm();
    return s.p1 + base*r;
}

class Circle {
public:
    Point c;                    // 中心
    double r;                   // 半径
    Circle(Point c = Point(), double r = 0.0): c(c), r(r) {}
};

double arg(Vector p) {
    return atan2(p.y, p.x);
}
Vector polar(double a, double r) {
    return Point(cos(r)*a, sin(r)*a);
}

pair<Point, Point> getCrossPoints(Circle c1, Circle c2) {
    double d = (c2.c - c1.c).abs();
    double a = acos( (c1.r*c1.r + d*d - c2.r*c2.r)/(2*c1.r*d) );
    double t = arg(c2.c - c1.c);
    return make_pair(c1.c + polar(c1.r, t+a), c1.c + polar(c1.r, t-a));
}

int main(void) {
    double c1x, c1y, c1r, c2x, c2y, c2r;
    cin >> c1x >> c1y >> c1r
        >> c2x >> c2y >> c2r;

    Circle c1(Point(c1x, c1y), c1r), c2(Point(c2x, c2y), c2r);
    pair<Point, Point> ans = getCrossPoints(c1, c2);

    if (ans.first < ans.second) {
        printf("%.9f %.9f %.9f %.9f\n",
               ans.first.x, ans.first.y, ans.second.x, ans.second.y);
    } else {
        printf("%.9f %.9f %.9f %.9f\n",
               ans.second.x, ans.second.y, ans.first.x, ans.first.y);
    }

    return 0;
}

