#include <bits/stdc++.h>
using namespace std;

const double eps = 1e-9;
bool same(double a, double b) { return abs(a - b) < eps; }

struct P {
    double x, y;
    P() : x(0), y(0) {}
    P(double x, double y) : x(x), y(y) {}
    P operator + (P b) { return P(x + b.x, y + b.y); }
    P operator - (P b) { return P(x - b.x, y - b.y); }
    P operator * (double b) { return P(x * b, y * b); }
    P operator / (double b) { return P(x / b, y / b); }
    double operator * (P b) { return x * b.x + y * b.y; }
    double operator ^ (P b) { return x * b.y - y * b.x; }
    double abs() { return hypot(x, y); }
    P unit() { return *this / abs(); }
    P spin(double o) {
        double c = cos(o), s = sin(o);
        return P(c * x - s * y, s * x + c * y);
    }
};

struct L {
    // ax + by + c = 0
    double a, b, c, o;
    P pa, pb;
    L() : a(0), b(0), c(0), o(0), pa(), pb() {}
    L(P pa, P pb) : a(pa.y - pb.y), b(pb.x - pa.x), c(pa ^ pb), o(atan2(-a, b)), pa(pa), pb(pb) {}
    P project(P p) { return pa + (pb - pa).unit() * ((pb - pa) * (p - pa) / (pb - pa).abs()); }
    P reflect(P p) { return p + (project(p) - p) * 2; }
    double get_ratio(P p) { return (p - pa) * (pb - pa) / ((pb - pa).abs() * (pb - pa).abs()); }
};

struct C {
    P c;
    double r;
    C() : r(0) {}
    C(P c, double r) : c(c), r(r) {}
};

bool parallel(L x, L y) { return same(x.a * y.b, x.b * y.a); }

P intersect(L x, L y) { return P(-x.b * y.c + x.c * y.b, x.a * y.c - x.c * y.a) / (-x.a * y.b + x.b * y.a); }

#define sq(x) ((x) * (x))
vector<P> intersect(C a, C b) {
    if (a.r > b.r) swap(a, b);
    double d = (a.c - b.c).abs();
    vector<P> ps;
    if (same(a.r + b.r, d)) ps.push_back(a.c + (b.c - a.c).unit() * a.r);
    else if (a.r + b.r > d && d + a.r >= b.r) {
        double o = acos((sq(a.r) + sq(d) - sq(b.r)) / (2 * a.r * d));
        P i = (b.c - a.c).unit();
        ps.push_back(a.c + i.spin(o) * a.r);
        ps.push_back(a.c + i.spin(-o) * a.r);
    }
    return ps;
}

int main() {
    C c[2];
    for (int i = 0; i < 2; ++i) cin >> c[i].c.x >> c[i].c.y >> c[i].r;
    vector<P> ps = intersect(c[0], c[1]);
    if (ps.size() == 1u) ps.push_back(ps[0]);
    if (ps[1].x < ps[0].x || (same(ps[0].x, ps[1].x) && ps[1].y < ps[0].y)) swap(ps[0], ps[1]);
    cout << fixed << setprecision(10) << ps[0].x << " " << ps[0].y << " " << ps[1].x << " " << ps[1].y << endl;
}

#include<bits/stdc++.h>
using namespace std;
typedef complex<double> point;

vector<point> CrossPoints_CC(point a,double ra,point b,double rb){
  vector<point> res;
  b-=a;
  if(abs(b)>ra+rb)return res;
  double c=(ra+rb+abs(b))/2;
  c=2*sqrt(c*(c-ra)*(c-rb)*(c-abs(b)))/abs(b);
  double d=sqrt(ra*ra-c*c);
  res.push_back(point(d,c));
  if(c||1)res.push_back(point(d,-c));
  for(int i=0;i<(int)res.size();i++)res[i]=res[i]*b/abs(b)+a;
  return res;
}

int main(){
  point c1,c2;
  double a,b,r1,r2;
  cin>>a>>b>>r1;
  c1=point(a,b);
  cin>>a>>b>>r2;
  c2=point(a,b);
  vector<point> ans=CrossPoints_CC(c1,r1,c2,r2);
  if(ans[0].real()>ans[1].real())swap(ans[0],ans[1]);
  if(ans[0].real()==ans[1].real())
    if(ans[0].imag()>ans[1].imag())swap(ans[0],ans[1]);
  for(int i=0;i<2;i++){
    if(i)cout<<' ';
    printf("%.8f %.8f",ans[i].real(),ans[i].imag());
  }
  cout<<endl;
  return 0;
}

