int main()
{
    double c1x, c1y, c1r, c2x, c2y, c2r;
	double dx, dy, d;
	scanf("%lf %lf %lf %lf %lf %lf", &c1x, &c1y, &c1r, &c2x, &c2y, &c2r);
	dx = c2x - c1x, dy = c2y - c1y;
	d = sqrt(dx * dx + dy * dy);

	double cos, sin, scale;
	double coord[4];
	if(d == c1r + c2r){
		cos = 1, sin = 0;
	}else{
		cos = (c1r * c1r + dx * dx + dy * dy - c2r * c2r) / (2 * c1r * d);
		sin = sqrt(1 - cos * cos);
	}
	scale = c1r / d;
	coord[0] = c1x + scale * (dx * cos - dy * sin);
	coord[1] = c1y + scale * (dx * sin + dy * cos);
	coord[2] = c1x + scale * (dx * cos + dy * sin);
	coord[3] = c1y + scale * (dy * cos - dx * sin);
    if(coord[0] > coord[2]){ swap(coord[0], coord[2]), swap(coord[1], coord[3]); }
    if(coord[0] == coord[2] && coord[1] > coord[3]){ swap(coord[1], coord[3]); }
    printf("%.12f %.12f %.12f %.12f\n", coord[0], coord[1], coord[2], coord[3]);

    return 0;
}

