  dx=c1.x;dy=c1.y;
  c1.x-=dx;c1.y-=dy;
  c2.x-=dx;c2.y-=dy;
  a=(c2.x*c2.x+c2.y*c2.y+c1.r*c1.r-c2.r*c2.r)/2;
  if(((c2.x*c2.x+c2.y*c2.y)*c1.r*c1.r-a*a)<0.0){return -1;}
  res[0].x = dx+
  ((a*c2.x+c2.y*sqrt((c2.x*c2.x+c2.y*c2.y)*c1.r*c1.r-a*a))
  /*---------------------------------------------------*/ /
                  (c2.x*c2.x+c2.y*c2.y));

  res[0].y = dy+
  ((a*c2.y-c2.x*sqrt((c2.x*c2.x+c2.y*c2.y)*c1.r*c1.r-a*a))
  /*---------------------------------------------------*/ /
                  (c2.x*c2.x+c2.y*c2.y));

  res[1].x = dx+
  ((a*c2.x-c2.y*sqrt((c2.x*c2.x+c2.y*c2.y)*c1.r*c1.r-a*a))
  /*---------------------------------------------------*/ /
                  (c2.x*c2.x+c2.y*c2.y));

  res[1].y = dy+
  ((a*c2.y+c2.x*sqrt((c2.x*c2.x+c2.y*c2.y)*c1.r*c1.r-a*a))
  /*---------------------------------------------------*/ /
                  (c2.x*c2.x+c2.y*c2.y));
  return 1;
}

