	class Vector2
	{
		public Vector2 ( decimal x, decimal y )
		{
			this.X = x;
			this.Y = y;
		}

		public decimal X { private set; get; }
		public decimal Y { private set; get; }

		public static Vector2 operator + ( Vector2 ob1, Vector2 ob2 )
		{
			return new Vector2 (ob1.X + ob2.X, ob1.Y + ob2.Y);
		}
		public static Vector2 operator - ( Vector2 ob1, Vector2 ob2 )
		{
			return new Vector2 (ob1.X - ob2.X, ob1.Y - ob2.Y);
		}
		public static Vector2 operator * ( Vector2 ob, decimal scalar )
		{
			return new Vector2 (ob.X * scalar, ob.Y * scalar);
		}

		private static decimal Sqrt ( decimal number )
		{
			return new decimal (Math.Sqrt (decimal.ToDouble (number)));
		}
		public decimal Length ()
		{
			return X * X + Y * Y;
		}
		public decimal Normalize ()
		{
			Vector2 tmp = new Vector2 (X, Y);
			return Sqrt (tmp.Length ());
		}
	}

	class Circle
	{
		public Circle ( decimal x, decimal y, decimal radius )
		{
			this.Point = new Vector2 (x, y);
			this.Radius = radius;
		}

		public Vector2 Point { private set; get; }
		public decimal Radius { private set; get; }

		public static Vector2[] CrossPoint ( Circle circle1, Circle circle2 )
		{
			decimal distance = (circle2.Point - circle1.Point).Normalize ();

			decimal tmp = (circle1.Radius * circle1.Radius + distance * distance - circle2.Radius * circle2.Radius) /
				(2 * circle1.Radius * distance);

			decimal radian = new decimal (Math.Acos (decimal.ToDouble (tmp)));

			Vector2 difference = circle2.Point - circle1.Point;
			decimal radian2 = new decimal (Math.Atan2 (decimal.ToDouble (difference.Y), decimal.ToDouble (difference.X)));

			return new Vector2[] { 
				circle1.Point + Polar (circle1.Radius, radian2 + radian),
				circle1.Point + Polar (circle1.Radius, radian2 - radian) 
			};
		}

		public static Vector2 Polar ( decimal radian, decimal radian2 )
		{
			double l = decimal.ToDouble (radian);
			double r = decimal.ToDouble (radian2);

			return new Vector2 (new decimal (Math.Cos (r) * l), new decimal (Math.Sin (r) * l));
		}
	}
}

