        double a = Math.Acos((RA * RA + d * d - RB * RB) / (2 * RA * d));
        var p = CB - CA;
        double t = Math.Atan2(p.Y, p.X);
        return new[] { CA + Polar(RA, t + a), CA + Polar(RA, t - a) };
    }
    private Point Polar(double a, double r)
    {
        return new Point(Math.Cos(r) * a, Math.Sin(r) * a);
    }

