public class Program
{
    public void Solve()
    {
        var CA = new Point(Reader.Double(), Reader.Double());
        double RA = Reader.Double();
        var CB = new Point(Reader.Double(), Reader.Double());
        double RB = Reader.Double();
        var P = Intersection(CA, RA, CB, RB);
        if (P.Length == 1) Console.WriteLine(P[0] + " " + P[0]);
        else Console.WriteLine(P[0] + " " + P[1]);
        Console.ReadLine();
    }

    public Point[] Intersection(Point CA, double RA, Point CB, double RB)
    {
        const double EPS = 1e-9;
        Debug.Assert(CA != CB || RA != RB);

        double d = CA.Distance(CB);
        if (d > RA + RB || Math.Abs(RA - RB) - d > EPS) return new Point[0];

        double a = (RA * RA - RB * RB + d * d) / (2 * d);
        double h = Math.Sqrt(RA * RA - a * a);
        Point p = CA + (CB - CA) * (a / d);
        double dx = CB.X - CA.X;
        double dy = CB.Y - CA.Y;

        var P1 = new Point(p.X + h * dy / d, p.Y - h * dx / d);
        if (Math.Abs(d - (RA + RB)) < EPS || Math.Abs(Math.Abs(RA - RB) - d) < EPS)
            return new Point[1] { P1 };
        var P2 = new Point(p.X - h * dy / d, p.Y + h * dx / d);
        if (P1.CompareTo(P2) <= 0) return new Point[2] { P1, P2 };
        return new Point[2] { P2, P1 };
    }

    public struct Point : IEquatable<Point>, IComparable<Point>
    {
        public static readonly double Eps = 1e-10;
        public double X, Y;
        public string Label;

        public Point(double x, double y) { X = x; Y = y; Label = ""; }
        public Point(double x, double y, string s) { X = x; Y = y; Label = s; }

        public static Point operator +(Point a, Point b)
        {
            return new Point(Add(a.X, b.X), Add(a.Y, b.Y));
        }
        public static Point operator -(Point a, Point b)
        {
            return new Point(Add(a.X, -b.X), Add(a.Y, -b.Y));
        }
        public static Point operator *(double d, Point p)
        {
            return new Point(p.X * d, p.Y * d);
        }
        public static Point operator *(Point p, double d) { return d * p; }
        public double Norm { get { return Math.Sqrt(Add(X * X, Y * Y)); } }
        public double Distance(Point b) { return (b - this).Norm; }
        public double Dot(Point b) { return Add(X * b.X, Y * b.Y); }
        public double Det(Point b) { return Add(X * b.Y, -Y * b.X); }
        public bool Equals(Point b) { return X == b.X && Y == b.Y; }
        public override bool Equals(object obj) { return Equals((Point)obj); }
        public static bool operator ==(Point a, Point b) { return a.Equals(b); }
        public static bool operator !=(Point a, Point b) { return !a.Equals(b); }
        public int CompareTo(Point b) { return X != b.X ? Math.Sign(X - b.X) : Math.Sign(Y - b.Y); }
        public override int GetHashCode() { return (int)(X * 100000 + Y); }
        public override string ToString() { return X + " " + Y; }

        private static double Add(double a, double b)
        {
            if (Math.Abs(a + b) < Eps * (Math.Abs(a) + Math.Abs(b))) return 0;
            return a + b;
        }
    }
}


