    struct Point
    {
        static readonly double EPS = 1E-10;
        public double x, y;
        public Point(double x, double y)
        {
            this.x = x;
            this.y = y;
        }

        public static Point operator +(Point a, Point b) { return new Point(a.x + b.x, a.y + b.y); }
        public static Point operator -(Point a, Point b) { return new Point(a.x - b.x, a.y - b.y); }
        public static Point operator *(Point a, double k) { return new Point(k * a.x, k * a.y); }
        public static Point operator /(Point a, double k) { return new Point(a.x / k, a.y / k); }

        public double Abs { get { return Math.Sqrt(Norm); } }  
        public double Norm { get { return x * x + y * y; } }
        public double Arg { get { return Math.Atan2(y, x); } }
        public double Dot(Point p) { return x * p.x + y * p.y; }
        public double Cross(Point p) { return x * p.y - y * p.x; }
        public double GetDistance(Point p) { return (this - p).Abs; }

        public static bool operator <(Point a, Point b) { return Math.Abs(a.x - b.x) > EPS ? a.x < b.x : a.y < b.y; }
        public static bool operator >(Point a, Point b) { return Math.Abs(a.x - b.x) > EPS ? a.x > b.x : a.y > b.y; }
        public static bool operator ==(Point a, Point b) { return Math.Abs(a.x - b.x) < EPS && Math.Abs(a.y - b.y) < EPS; }
        public static bool operator !=(Point a, Point b) { return Math.Abs(a.x - b.x) >= EPS || Math.Abs(a.y - b.y) >= EPS; }

        public override bool Equals(object p){  return Math.Abs(x - ((Point)p).x) < EPS && Math.Abs(y - ((Point)p).y) < EPS; }
        public override int GetHashCode(){ return x.GetHashCode() ^ y.GetHashCode(); }

        public int CCW(Point b)
        {
            if (Cross(b) > EPS) return 1;
            if (Cross(b) < -EPS) return -1;
            if (Dot(b) < -EPS) return 2;
            if (Norm < b.Norm) return -2;
            return 0;
        }
    }
    struct Segment
    {
        public Point p1, p2;
        public Segment(Point p1, Point p2)
        {
            this.p1 = p1;
            this.p2 = p2;
        }
        public Point Projection(Point p)
        {
            Point b = p2 - p1;
            double r = b.Dot(p - p1) / b.Norm;
            return p1 + b * r;
        }
        public Point Reflection(Point p)
        {
            return p + (Projection(p) - p) * 2;
        }
        public double GetD_LP(Point p)
        {
            return Math.Abs((p - p1).Cross(p2 - p1) / (p2 - p1).Abs);
        }
        public double GetD_SP(Point p)
        {
            if ((p - p1).Dot(p2 - p1) < 0) return (p - p1).Abs;
            if ((p - p2).Dot(p1 - p2) < 0) return (p - p2).Abs;
            return GetD_LP(p);
        }
        public bool Intersect(Segment s)
        {
            return (p2 - p1).CCW(s.p1 - p1) * (p2 - p1).CCW(s.p2 - p1) <= 0 &&
                (s.p2 - s.p1).CCW(p1 - s.p1) * (s.p2 - s.p1).CCW(p2 - s.p1) <= 0;
        }
        public double GetDistance(Segment s)
        {
            if (this.Intersect(s)) return 0;
            return Math.Min(Math.Min(GetD_SP(s.p1), GetD_SP(s.p2)), Math.Min(s.GetD_SP(p1), s.GetD_SP(p2)));
        }
        public Point GetCrossPoint(Segment s)
        {
            Point b = s.p2 - s.p1;
            double d1 = Math.Abs(b.Cross(p1 - s.p1));
            double d2 = Math.Abs(b.Cross(p2 - s.p1));
            double t = d1 / (d1 + d2);
            return p1 + (p2 - p1) * t;
        }
    }
    struct Circle
    {
        public Point c; double r;
        public Circle(Point c, double r)
        {
            this.c = c;
            this.r = r;
        }
        public bool Intersect(Segment l)
        {
            return l.GetD_LP(c) <= r;
        }
        public KeyValuePair<Point, Point> GetCrossPoints(Segment l)
        {
            Point pr = l.Projection(c);
            Point e = (l.p2 - l.p1) / (l.p2 - l.p1).Abs;
            double b = Math.Sqrt(r * r - (pr - c).Norm);
            return new KeyValuePair<Point, Point>(pr + e * b, pr - e * b);
        }
        public KeyValuePair<Point, Point> GetCrossPoints(Circle c2)
        {
            double d = (c - c2.c).Abs;
            double a = Math.Acos((r * r + d * d - c2.r * c2.r) / (2 * r * d));
            double t = (c2.c - c).Arg;
            Point p1 = c + new Point(Math.Cos(t + a) * r, Math.Sin(t + a) * r);
            Point p2 = c + new Point(Math.Cos(t - a) * r, Math.Sin(t - a) * r);
            return new KeyValuePair<Point, Point>(p1, p2);
        }

    }
}

