// solve
func solve(sc *bufio.Scanner) {

	sc.Scan()

	circle := strSliceToFloat64(strings.Split(sc.Text(), " "))
	c1c := []float64{
		circle[0],
		circle[1],
	}
	c1r := circle[2]

	sc.Scan()
	circle = strSliceToFloat64(strings.Split(sc.Text(), " "))
	c2c := []float64{
		circle[0],
		circle[1],
	}
	c2r := circle[2]

	p := subVector(c2c, c1c)
	d := absVector(p)
	cos := (d*d + c1r*c1r - c2r*c2r) / (2 * d * c1r)
	angle1 := math.Acos(cos)
	angle2 := math.Atan2(p[1], p[0])

	result := make([][]float64, 2)
	result[0] = []float64{
		c1c[0] + c1r*math.Cos(angle2+angle1),
		c1c[1] + c1r*math.Sin(angle2+angle1),
	}
	result[1] = []float64{
		c1c[0] + c1r*math.Cos(angle2-angle1),
		c1c[1] + c1r*math.Sin(angle2-angle1),
	}

	i := 1
	if result[0][0] > result[1][0] {
		i = 0
	} else if (result[0][0] == result[1][0]) {
		if result[0][1] > result[1][1] {
			i = 0
		}
	}
	fmt.Printf("%.10f %.10f %.10f %.10f\n", result[1-i][0], result[1-i][1], result[i][0], result[i][1])
}

