        point<numeric_type> p;
        numeric_type r;
    };

    template <typename numeric_type>
    std::istream &operator>>(std::istream &is, circle<numeric_type> &c)
    {
        return is >> c.p >> c.r;
    }

    template <typename numeric_type, typename float_type = double>
    std::vector<point<numeric_type>> tangent_circle_point(circle<numeric_type> c, point<numeric_type> p)
    {
        float_type sin2 = c.r * c.r / norm(p - c.p);
        if (sign(1 - sin2) < 0)
            return {};
        if (sign(1 - sin2) == 0)
            return {p};
        point<numeric_type> z(1 - sin2, sqrt(sin2) * sqrt(1 - sin2));
        return {p + (c.p - p) * conj(z), p + (c.p - p) * z};
    }

} 

