    P contact2(r*(r*x - y*sqrt(x*x + y*y - r*r))/(x*x + y*y), r*(r*y + x*sqrt(x*x + y*y - r*r))/(x*x + y*y));
    if (contact1 == contact2){
        cout << contact1.real() << " " << contact1.imag() << endl;
    }else{
        if(contact2 < contact1){
            swap(contact1, contact2);
        }
        printf("%.10f %.10f\n", contact1.real() + cx, contact1.imag() + cy);
        printf("%.10f %.10f\n", contact2.real() + cx, contact2.imag() + cy);
    }
}

