using namespace std;

typedef complex<double> P;

struct L : public vector<P>{
    L(const P &a, const P &b){
        push_back(a), push_back(b);
    }
};

struct C {
    P p; double r;
    C(const P &p, double r) : p(p), r(r) { }
};

const double EPS = 1e-8;

namespace std {
  bool operator < (const P& a, const P& b) {
    return real(a) != real(b) ? real(a) < real(b) : imag(a) < imag(b);
  }
}

double cross(const P &a, const P &b){
    return imag(conj(a)*b);
}

double dot(const P &a, const P &b){
    return real(conj(a)*b);
}

int ccw(P a, P b, P c){
    b -= a;
    c -= a;
    if(cross(b,c) > 0) return +1;
    if(cross(b,c) < 0) return -1;
    if(dot(b,c) < 0) return +2;
    if(norm(b) < norm(c)) return -2;
    return 0;
}

P crosspoint(const L &l, const L &m) {
    double A = cross(l[1] - l[0], m[1] - m[0]);
    double B = cross(l[1] - l[0], l[1] - m[0]);
    if (abs(A) < EPS && abs(B) < EPS) return m[0]; // same line
    return m[0] + B / A * (m[1] - m[0]);
}

void tangentToCircle(double px, double py, double cx, double cy, double r){
    double x = px - cx;
    double y = py - cy;
    P contact1(r*(r*x + y*sqrt(x*x + y*y - r*r))/(x*x + y*y), r*(r*y - x*sqrt(x*x + y*y - r*r))/(x*x + y*y));
    P contact2(r*(r*x - y*sqrt(x*x + y*y - r*r))/(x*x + y*y), r*(r*y + x*sqrt(x*x + y*y - r*r))/(x*x + y*y));
    if (contact1 == contact2){
        cout << contact1.real() << " " << contact1.imag() << endl;
    }else{
        if(contact2 < contact1){
            swap(contact1, contact2);
        }
        cout << contact1.real() + cx << " " << contact1.imag() + cy << endl;
        cout << contact2.real() + cx << " " << contact2.imag() + cy << endl;
    }
}

int main(){
    double px, py, cx, cy, r;
    cin >> px >> py >> cx >> cy >> r;

    tangentToCircle(px, py, cx, cy, r);
    return 0;
}

