  cout << fixed << setprecision(10);
  long long a, b, c, d, e;
  cin >> a >> b >> c >> d >> e;
  CP p, q;
  contactCP(Circle(c, d, e), CP(a, b), p, q);
  cout << p.X << " " << p.Y << endl
       << q.X << " " << q.Y << endl;

  return 0;
}

