vector<Vec> shiftConvexHull(vector<Vec> A) {
    double my = INF, mx = INF; int st = -1;
    for (int i = 0; i < A.size(); i ++) {
        if (A[i].y < my || (A[i].y == my && A[i].x < mx) ) {
            my = A[i].y; mx = A[i].x; st = i;
        }
    }
    vector<Vec> ans;
    for (int i = 0; i < A.size(); i ++) {
        ans.push_back(A[(i+st)%A.size()]);
    }
    return ans;
}

vector<Vec> convexHull(vector<Vec> v) {
    sort(v.begin(), v.end());
    vector<Vec> ans; ans.resize(2*v.size());
    int top = 0, n = v.size();
    if(n <= 1) return v;

    for(int i = 0; i < n; ans[top++] = v[i++]) {
        while (top > 1 && ((ans[top-1] - ans[top-2]) ^ (v[i] - ans[top-1])) < EPS) -- top;
    }
    for(int i = n-2, t = top; i >= 0; ans[top++] = v[i--]) {
        while (top > t && ((ans[top-1] - ans[top-2]) ^ (v[i] - ans[top-1])) < EPS) -- top;
    } 
    while(ans.size() >= top) ans.pop_back();
    return ans;
}
double PolyArea(vector<Vec> v) {
    double ans = 0;
    int n = v.size();
    for(int i = 0; i < v.size(); i ++) {
        ans = ans + (v[i] ^ v[(i+1)%n]);
    }
    return abs(ans) / 2;
}
vector<Vec> convexCut(vector<Vec> v, Vec P1, Vec P2) {
    vector<Vec> ans;
    for(int i=0;i<v.size();i++) {
        int d1 = sgn((P2-P1)^(v[i]-P1));
        int d2 = sgn((P2-P1)^(v[(i+1)%v.size()]-P1));
        if (d1 >= 0) ans.push_back(v[i]);
        if (d1 * d2 < 0) ans.push_back(Lineintersect(Line(P1,P2), Line(v[i],v[(i+1)%v.size()])));
    }
    return ans;
}
double cloestPair(vector<Vec> v) {
    if (v.size() <= 1) return INF;

    sort(v.begin(), v.end());
    vector<Vec> vL, vR;
    int mid = v.size() / 2;
    for (int i = 0; i < v.size(); i ++) {
        if (i < mid)
            vL.push_back(v[i]);
        else
            vR.push_back(v[i]);
    }
    double d = min(cloestPair(vL), cloestPair(vR));

    vector<Vec> strip;
    for (int i = 0; i < v.size(); i ++) {
        if (abs(v[mid].x - v[i].x) <= d + EPS) 
            strip.push_back(v[i]);
    }

    for (int i = 0; i < strip.size(); i ++) swap(strip[i].x, strip[i].y);
    sort(strip.begin(), strip.end());
    for (int i = 0; i < strip.size(); i ++) swap(strip[i].x, strip[i].y);

    for (int i = 0; i < strip.size(); i ++) {
        for (int j = i + 1; j < strip.size(); j ++) {
            if (strip[j].y - strip[i].y > d + EPS) break;
            d = min(d, (strip[j]-strip[i]).len());
        }
    }
    return d;
}
bool onRight(Line L1, Line L2, Line L3) {
    Vec P = Lineintersect(L2, L3);
    return ((L1.A2 - L1.A1) ^ (P - L1.A1)) < 0;
}
vector<Vec> halfPlaneIntersection(vector<Line> L) {
    sort(L.begin(), L.end());

    int head = 0, tail = 0, cnt = 0; //[head, tail)
    Line que[L.size()+1];
    for (int i = 0; i < (int)L.size() - 1; i ++) {
        if (abs(L[i].getAng() - L[i+1].getAng()) < EPS) continue;
        L[cnt ++] = L[i];
    }

    L[cnt ++] = L[(int)L.size() - 1];

    for (int i = 0; i < cnt; i ++) {
        while (tail - head > 1 && onRight(L[i], que[tail-1], que[tail-2])) -- tail;
        while (tail - head > 1 && onRight(L[i], que[head], que[head+1])) ++ head;
        que[tail++] = L[i];
    }
    while (tail - head > 1 && onRight(que[tail-1], que[head], que[head+1])) ++ head;
    while (tail - head > 1 && onRight(que[head], que[tail-1], que[tail-2])) -- tail;

    vector<Vec> ans;
    for (int i = head; i < tail - 1; i ++) {
        ans.push_back(Lineintersect(que[i], que[i+1]));
    }
    ans.push_back(Lineintersect(que[tail - 1], que[head]));

    return ans;
}
vector<Vec> Minkowski(vector<Vec> a, vector<Vec> b) {
    vector<Line> L;
    for (int i = 0; i < a.size(); i ++) {
        L.push_back(Line(a[i], a[(i+1)%(int)a.size()]));
    }
    for (int i = 0; i < b.size(); i ++) {
        L.push_back(Line(b[i], b[(i+1)%(int)b.size()]));
    }
    sort(L.begin(), L.end());
    vector<Vec> ans;

    Vec now = Vec(0, 0); 
    ans.push_back(now);
    for (int i = 0; i < L.size(); i ++) {
        ans.push_back(now + L[i].A2 - L[i].A1);
        now = now + L[i].A2 - L[i].A1;
    }

    Vec LD = Vec(INF,INF);
    for (int i = 0; i < ans.size(); i ++) {
        LD = min(LD, ans[i]);
    }
    for (int i = 0; i < ans.size(); i ++) {
        ans[i] = ans[i] + (a[0] + b[0] - LD);        
    }
    return ans;
}
