#include<bits/stdc++.h>
using namespace std;
typedef complex<double> P;

namespace std{
  bool operator < (const P &a,const P &b){
    return (a.real()<b.real()||(a.real()==b.real() && a.imag()<b.imag()));
  }
}

double eps=1e-10;

double Sqrt(double x){
  return sqrt(max(0.0,x));
}

struct S{
  P p,q;
  S(P p,P q):p(p),q(q){}
};

struct C{
  P p;
  double r;
  C(P p,double r):p(p),r(r){}
  C(double x,double y,double r):p(P(x,y)),r(r){}
};

S getTangent(C a,P p){
  P base=a.p-p;
  double w=Sqrt(norm(base)-a.r*a.r);
  P s=p+base*P(w,a.r)/norm(base)*w;
  P t=p+base*P(w,-a.r)/norm(base)*w;
  return S(s,t);
}

int main(){
  double ax,ay,ar,bx,by;
  cin>>bx>>by>>ax>>ay>>ar;
  S ans=getTangent(C(ax,ay,ar),P(bx,by));
  if(ans.q<ans.p)swap(ans.q,ans.p);
  printf("%.10f %.10f\n",ans.p.real(),ans.p.imag());
  printf("%.10f %.10f\n",ans.q.real(),ans.q.imag());
  return 0;
}

