bool equals(point const& a, point const& b) {
  return abs(a - b) < EPS;
}

bool equals(Real a, Real b) {
  return abs(a - b) < EPS;
}

vector<point> tangent_points(circle const& c, point const& p) {
  vector<point> ret;
  const Real sec2 = norm(p - c.p);
  const Real tan2 = max(Real(0), sec2 - c.r * c.r);
  const point nv = (p - c.p) * c.r * c.r / sec2;
  const point pv = (p - c.p) * point(0, -1) * c.r * sqrt(tan2) / sec2;
  ret.push_back(c.p + nv + pv);
  ret.push_back(c.p + nv - pv);
  return ret;
}

int main() {

