int sign(let x) { return (x < -eps) ? -1 : (x > eps ? 1 : 0); }

struct Point {
  let x, y;
  let dist() {
    return sqrt(x * x + y * y);
  }
  Point Rotate(let sink, let cosk) {
    return (Point) {
      x * cosk - y * sink, x * sink + y * cosk
    };
  }
  Point Rotate(Point v) {
    let z = v.dist();
    return Rotate(v.y / z, v.x / z);
  }
  friend Point operator + (Point a, Point b) {
    return (Point) {a.x + b.x, a.y + b.y};
  }
  friend Point operator - (Point a, Point b) {
    return (Point) {a.x - b.x, a.y - b.y};
  }
  Point operator ~ () {
    return (Point) {x, -y};
  }
  friend bool operator < (Point a, Point b) {
    if (sign(a.x - b.x) == 0) return sign(a.y - b.y) < 0;
    return sign(a.x - b.x) < 0;
  }
  void EchoWith(char c) {
    printf("%.8Lf %.8Lf", x, y);
    putchar(c);
  }
};

typedef Point Vec;

struct Segment {
  Point s, e;
  Vec v;

  Segment operator + (Vec x) {
    return (Segment) {
      s + x, e + x, v
    };
  }

  Segment Rotate(let sink, let cosk) {
    return (Segment) {
      s.Rotate(sink, cosk), e.Rotate(sink, cosk), v.Rotate(sink, cosk)
    };
  }

  Segment Rotate(Vec v) {
    let z = v.dist();
    return Rotate(v.y / z, v.x / z);
  }

};

struct Circle {
  Point o;
  let r;
};

Segment MoveCutX(Point a, Point b, Point an, Point bn) {
  // a.EchoWith('|'), b.EchoWith('\n');
  // an.EchoWith('|'), bn.EchoWith('\n');
  // puts("");

  Point ov = b - a, tv = bn - an, dx = a - an;
  Segment s = (Segment) {
    (Point) {an.x, 0}, (Point) {bn.x, 0}
  };
  return s.Rotate(~tv).Rotate(ov) + dx;
}

pair<Point, Point> Tangent(Circle c, Point p) {
  let d = (p - c.o).dist();
  Point up = (Point) {sqrt(d * d - c.r * c.r), c.r},
        dp = ~up;
  Segment us = MoveCutX(p, c.o, (Point) {0, 0}, up),
          ds = MoveCutX(p, c.o, (Point) {0, 0}, dp);
  up = us.e, dp = ds.e;
  if (dp < up) swap(up, dp);
  return make_pair(up, dp);
}


