// 線分abから見たcの位置
enum CCW{LEFT=1, RIGHT=2, BACK=4, FRONT=8, ON_SEG=16};
int ccw(P a, P b, P c) {
	P p = (c-a)/(b-a);
	if(sgn(imag(p)) > 0) return LEFT;
	if(sgn(imag(p)) < 0) return RIGHT;
	if(sgn(real(p)) < 0) return BACK;
	if(sgn(real(p)-1) > 0) return FRONT;
	return ON_SEG;
}

// 点aを通る接線を返す
// pl = ((a, 接線と円の交点), (a, 接線と円の交点))
pair<L,L> tangent(C c, P a) {
    pair<L,L> pl;
    if(sgn(abs(a-c.c)-c.r) == 0) { // 点 a が円周上にあるとき
        auto normal = [](P a) {
            L vp({a*P(0,1), a*P(0,-1)});
            return vp;
        };
        L l = normal(a-c.c); // 法線ベクトル
        l.first += a; l.second += a;
        pl.first = pl.second = l;
    } else if(sgn(abs(a-c.c)-c.r) > 0) { // 点 a が円の外側にあるとき
        double xp = a.real() - c.c.real();
        double yp = a.imag() - c.c.imag();
        double A = sqrt(xp*xp + yp*yp - c.r*c.r);
        double B = xp*xp + yp*yp;
        P p1(c.r*(xp*c.r + yp*A)/B , c.r*(yp*c.r - xp*A)/B);
        P p2(c.r*(xp*c.r - yp*A)/B , c.r*(yp*c.r + xp*A)/B);
        pl = make_pair(L(a ,p1+c.c), L(a ,p2+c.c));
    } else { // 点 a が円の内側にあるとき
        pl.first = pl.second = L(P(INF,INF), P(INF,INF));
    }
    return pl;
}

