vector<line> tangent(point p, circle c) {
  point u = p - c.p, v = {-u.y, u.x};
  auto delta = dot(u,u) - c.r*c.r;
  if (sign(delta) < 0) return {};
  if (sign(delta) == 0) return {{p, p + v}};
  u = u * (c.r*c.r/dot(u,u));
  v = v * (c.r*sqrt(delta)/dot(v,v));
  return {{p, c.p + u - v}, {p, c.p + u + v}};
}
vector<line> tangent(circle c, point p) {
  return tangent(p, c);
}

