        o.read(), scanf("%lf", &r);
    }
    int inside(point p){ // 1: inside, 2: on the circle, 0: outside
        int sgn = dcmp((o - p).len() - r) < 0; if (sgn < 0) return 1; if (!sgn) return 2; return 0;
    }
};

int check_CC(circle C1, circle C2){ // return the number of common tangents of two circles C1 and C2
//4: xiang_li, 3: wai_qie, 2: xiang_jiao, 1: nei_qie, 0: nei_han
    db d =(C1.o - C2.o).len();
    if (dcmp(d - C1.r - C2.r) > 0) return 4;
    if (!dcmp(d - C1.r - C2.r)) return 3;
    if (!dcmp(d - fabs(C1.r - C2.r))) return 1;
    if (dcmp(d - fabs(C1.r - C2.r)) > 0 && dcmp(d - C1.r - C2.r) < 0) return 2;
    return 0;
}

vector<point> inter_CL(circle C, point A, point B){ // return the intersection of a circle and a line
// the result give in the direction from A -> B
    point p_proj = proj_SP(A, B, C.o);
    db d = C.r * C.r - (C.o - p_proj).len2();
    if (dcmp(d) < 0) return {};
    point delta = (B - A).unit() * sqrt(max((db)0, d)); return {p_proj - delta, p_proj + delta};
}

vector<point> inter_CL(circle C, line L){ // the same as the former one, but argument is different
    return inter_CL(C, L.p[0], L.p[1]);
}

vector<point> inter_CC(circle C1, circle C2){ // return the cross points in counter-clockwised order
    int sgn = check_CC(C1, C2);
    if (!sgn || sgn == 4) return {};
    point dir = C2.o - C1.o;
    db d = dir.len(); db cosA = (d * d + C1.r * C1.r - C2.r * C2.r) / (2 * C1.r * d);
    db sinA = sqrt(1 - cosA * cosA); db h = C1.r * sinA; db base = C1.r * cosA;
    point k = dir.unit(), b = k.norm() * h;
    return {C1.o + k * base - b, C1.o + k * base + b};
}

vector<point> tange_CP(circle C, point P){ // get then tangent point of a point P for a circle C, return in counter-clockwise order
    point OP = P - C.o;
    if (dcmp(OP.len() - C.r) < 0) return {};
    db cosA = C.r / OP.len(); db base = C.r * cosA; db h = sqrt(max((db)0, C.r * C.r - base * base));
    point dir = OP.unit().norm();
    return {C.o + OP.unit() * base - dir * h, C.o + OP.unit() * base + dir * h}; 
}

