}

// Actual Solver
inline void solve()
{
    cout << fixed << setprecision(10);
    Point p;
    Circle c;
    p.read();
    c.read();
    Line u, v;
    c.tangent(p, u, v);
    Point p1 = u.s == p ? u.e : u.s;
    Point p2 = v.s == p ? v.e : v.s;
    se(p1, p2);
    p1.write();
    cout << '\n';
    p2.write();
    cout << '\n';
}

