//点pを通る円cの接線について、円cで接する接点を返す
vector<Point> tangentPoints(Circle c, Point p){
	//複素数表示α=a+ib, β=c+id の積 αβ=(a+ib)(c+id)=(ac–bd)+i(ad+bc)
	auto mul = [](Point a, Point b){
		return Point(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
	};
	vector<Point> res;
	Point a = c.c;
	double ar = c.r;
    double sin = ar / (p-a).abs();
	if(sin > 1.+EPS) return res;
	double t = M_PI_2 - asin(min(sin, 1.));
	res.push_back(a + mul((p-a), polar(sin, t)));
	if(!equals(sin, 1.)) res.push_back(a + mul((p-a), polar(sin, -t)));
	return res;
}

