double Dot(Vector A, Vector B) { return A.x * B.x + A.y * B.y; }
double Length(Vector A) { return sqrt(Dot(A, A)); }
double Angle(Vector A, Vector B) { return acos(Dot(A, B) / Length(A) / Length(B)); }
double Cross(Vector A, Vector B) { return A.x * B.y - A.y * B.x; }
double Area2(Point A, Point B, Point C) { return Cross(B - A, C - A); }
Vector Rotate(Vector A, double rad) { return Vector(A.x * cos(rad) - A.y * sin(rad), A.x * sin(rad) + A.y * cos(rad)); }
Vector Normal(Vector A) { double L = Length(A); return Vector(-A.y / L, A.x / L); } //A is not zero vector
//point and line
Point GetLineIntersection(Point P, Vector v, Point Q, Vector w) {
	Vector u = P - Q;
	double t = Cross(w, u) / Cross(v, w);
	return P + v * t;
}//two lines only have one intersection and Cross(v,w) is not zero
double DistanceToLine(Point P, Point A, Point B) {
	Vector v1 = B - A, v2 = P - A;
	return fabs(Cross(v1, v2)) / Length(v1);
}
double DistanceToSegment(Point P, Point A, Point B) {
	if (A == B) return Length(P - A);
	Vector v1 = B - A, v2 = P - A, v3 = P - B;
	if (dcmp(Dot(v1, v2)) < 0) return Length(v2);
	else if (dcmp(Dot(v1, v3)) > 0) return Length(v3);
	else return fabs(Cross(v1, v2)) / Length(v1);
}
Point GetLineProjection(Point P, Point A, Point B) {
	Vector v = B - A;
	return A = v * (Dot(v, P - A) / Dot(v, v));
}
bool SegmentProperIntersection(Point a1, Point a2, Point b1, Point b2) {
	double c1 = Cross(a2 - a1, b1 - a1), c2 = Cross(a2 - a1, b2 - a1), c3 = Cross(b2 - b1, a1 - b1), c4 = Cross(b2 - b1, a2 - b1);
	return dcmp(c1) * dcmp(c2) < 0 && dcmp(c3) * dcmp(c4) < 0;
}
bool OnSegment(Point p, Point a1, Point a2) {
	return dcmp(Cross(a1 - p, a2 - p)) == 0 && dcmp(Dot(a1 - p, a2 - p)) <= 0;
}
double angle(Vector v) { return atan2(v.y, v.x); }
//polygon
double ConvexPolygonArea(Point* p, int n) {
	double area = 0;
	for (int i = 1; i < n - 1; i++)
		area = Cross(p[i] - p[0], p[i + 1] - p[0]);
	return area / 2;
}
//circle
struct Circle {
	Point c;
	double r;
	Circle(Point c, double r) :c(c), r(r) {}
	Point point(double a) { return Point(c.x + cos(a) * r, c.y + sin(a) * r); }
};
int getTangents(Point p, Circle C, Vector* v) {
	Vector u = C.c - p;
	double dist = Length(u);
	if (dist < C.r)
		return 0;
	else if (dcmp(dist - C.r) == 0) {
		v[0] = Rotate(u, PI / 2);
		return 1;
	}
	else {
		double ang = asin(C.r / dist);
		double l = sqrt(dist * dist - C.r * C.r);
		//u = u / dist * l;
		v[0] = Rotate(u, -ang);
		v[0] = p + v[0] / sqrt(v[0].x * v[0].x + v[0].y * v[0].y) * l;
		v[1] = Rotate(u, ang);
		v[1] = p + v[1] / sqrt(v[1].x * v[1].x + v[1].y * v[1].y) * l;
		return 2;
	}
}

